mapper.select:
select *
from moim
where moim_id=#{moimId}

		select b.*, nvl(c.cnt, 0)cnt
		from moim_board b,
		(select count(*)cnt, target_id from comments
		where target_id in (
		select board_id from moim_board)
		and target_type=3
		group by target_id)c
		where b.board_id = c.target_id(+)
		and moim_id = #{moimId}
		and board_type = #{boardType}

select b.*, nvl(c.cnt, 0)cnt
from moim_board b,
(select count(*)cnt, target_id from comments
where target_id in (
select board_id from moim_board)
and target_type=3
group by target_id)c
where b.board_id = c.target_id(+)
and moim_id = #{moimId}
and board_type = #{boardType}

select c.comm_id, c.member_id as comment_writer, c.parent_comm_id,
c.target_id, c.content,
c.write_date as comment_date , m.board_id, m.board_type, m.moim_id
from comments c ,moim_board m
where c.target_id = m.board_id
and c.target_type = 3
and m.moim_id = #{moimId}
and m.board_type = #{boardType}
and m.board_id = #{boardId}

select * from moim_board
where board_id = #{boardId}
and board_type = #{boardType}
and moim_id = #{moimId}

select count(*)
from moim_member
where moim_id = #{moimId}

mapper.update:
DECLARE
v_member VARCHAR2(100) := #{memberId};
v_temp varchar2(100);
BEGIN
select role
into v_temp
from member
where member_id=v_member;
if v_temp =0 then
update member set role=2 where member_id= v_member;
elsif v_temp=1 then
update member set role=3 where member_id= v_member;
end if;
update moim_member
set
member_role = 2
where
member_id = v_member;
end;

mapper.select:
select MOIM_IMG,MOIM_NAME
from moim
where moim_id=#{moimId}

select member_id
from moim_member
where moim_id = #{moimId}

		DECLARE
		v_voteId NUMBER := #{voteId};
		v_memberId
		VARCHAR2(100) := #{memberId};
		v_itemSelect NUMBER := #{itemSelect};
		v_moimId NUMBER := #{moimId};
		BEGIN

		INSERT INTO moim_vote_ptp(MEMBER_ID,
		MOIM_ID, ITEM_SELECT, VOTE_ID)
		VALUES (v_memberId, v_moimId,
		v_itemSelect, v_voteId);

		UPDATE moim_vote_item SET cnt = cnt+1 where
		vote_id= v_voteId and item_id = v_itemSelect;
		END;

mapper.update:
		DECLARE
		v_voteId NUMBER := #{voteId};
		v_memberId VARCHAR2(100) := #{memberId};
		v_itemSelect NUMBER := #{itemSelect};
		v_moimId NUMBER := #{moimId};
		v_preSelect Number := #{preSelect};
		BEGIN

		update moim_vote_ptp
		set item_select = v_itemSelect
		where vote_id =
		v_voteId
		and moim_id = v_moimId
		and member_id = v_memberId;

		update moim_vote_item
		set cnt = cnt+1
		where item_id = v_itemSelect;

		update
		moim_vote_item
		set cnt = cnt-1
		where item_id = v_preSelect;

		END;

mapper.select:
select count(*) from
moim_vote_ptp
where member_id = #{memberId}
and moim_id = #{moimId}
and
item_select = #{itemSelect}
and vote_id = #{voteId}

select v.vote_id, nvl(p.item_select, 0)item_select
from moim_vote v,
(select * from moim_vote_ptp where member_id = #{memberId}) p
where v.vote_id = p.vote_id(+)
and v.moim_id = #{moimId}
order by v.vote_id

select count(*) from
moim_vote_ptp
where member_id = #{memberId}
and moim_id = #{moimId}
and
item_select = #{itemSelect}
and vote_id = #{voteId}

select v.vote_id, nvl(p.item_select, 0)item_select
from moim_vote v,
(select * from moim_vote_ptp where member_id = #{memberId}) p
where v.vote_id = p.vote_id(+)
and v.moim_id = #{moimId}
order by v.vote_id
