mapper.update:
UPDATE sns_post 
SET content = #{ content }, hashtag = #{ hashtag }
WHERE post_id = #{ postId }

mapper.select:
SELECT post_id, member_id, thumbnail, likes, cmts 
FROM sns_post
WHERE member_id = #{ memberId }

mapper.delete:
DELETE FROM follow
WHERE follower_id = #{ followerId }
AND following_id = #{ followingId }

mapper.select:
DECLARE
v_target_id NUMBER := 7;
v_member_id member.member_id%TYPE := 'user1';
v_status NUMBER;
BEGIN
SELECT
    (CASE WHEN NOT EXISTS (
        SELECT *
        FROM jjim
        WHERE target_id IN (
            SELECT target_id FROM jjim
            WHERE target_id = v_target_id
            AND member_id = v_member_id
            AND target_type = 2       
            )
     ) THEN 0 ELSE 1 END  
) test
INTO v_status
FROM dual;
IF v_status = 0 THEN
INSERT INTO jjim (target_id, target_type, member_id) VALUES(v_target_id, 2, v_member_id);
UPDATE sns_post SET likes = likes + 1 WHERE post_id = v_target_id;
ELSIF v_status = 1 THEN 
DELETE FROM jjim WHERE target_id= v_target_id AND target_type=2 AND member_id = v_member_id;
UPDATE sns_post SET likes = likes - 1 WHERE post_id = v_target_id;
END IF;
END

mapper.insert:
	INSERT INTO comments (
							comm_id,
							member_id,
							target_id,
							target_type,
							content,
							write_date
   					  )
   			 VALUES (
   						 comm_seq.nextval
   						,#{ memberId }
   						,#{ targetId }
   						,#{ targetType }
   						,#{ content }
   						,SYSDATE
   					)

mapper.update:
UPDATE comments SET content = #{ content },
					write_date = SYSDATE
WHERE comm_id = #{ commId }

mapper.select:
SELECT target_type, target_id, parent_comm_id, comm_id, member_id, content, write_date
FROM (  SELECT * FROM comments
        WHERE target_id = #{ targetId }
        AND target_type = #{ targetType }
      )
    START WITH parent_comm_id IS NULL
CONNECT BY PRIOR comm_id = parent_comm_id
ORDER SIBLINGS BY comm_id;

